/* eslint-disable no-restricted-globals */
import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
import axios from 'axios';


function App() {
  const [response, setResponse] = useState('');
  const [isMobile, setIsMobile] = useState(false);
  const url = 'https://www.googleapis.com/geolocation/v1/geolocate?key=AIzaSyB1C2pNGVn8FeFrcR9mt-eNiD2gCROazRc';

  const handleLocation = async (e) => {

    if (screen.width < 640 || screen.height < 480) setIsMobile(true);

    const {data} = await axios.post(url);

    if(isMobile){
      const serverApi = await axios.post('http://192.168.100.59:3000/location', { device: data, isMobile });
    }
    const serverApi = await axios.post('http://localhost:3000/location', { device: data, isMobile });

  }

  useEffect(() => {
    handleLocation();
  });

  return (
    <div className="App">
      <header className="App-header">
        <p>
          {response}
        </p>
       <button onClick={ handleLocation }>Click Me Please</button>
      </header>
    </div>
  );
}

export default App;
