/* eslint-disable no-restricted-globals */
import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
import axios from 'axios';


function App() {  
  const [lat, setLat] = useState('');
  const [log, setLog] = useState('');
  const [sv, setSv] = useState('');
  const [isMobile, setIsMobile] = useState(false);
  const url = 'https://www.googleapis.com/geolocation/v1/geolocate?key=AIzaSyB1C2pNGVn8FeFrcR9mt-eNiD2gCROazRc';

  const handleLocation = async (e) => {
    e.preventDefault();
    if (screen.width < 640 || screen.height < 480) setIsMobile(true);

    const { data } = await axios.post(url);

    if(isMobile){
      const sv = await axios.post('http://192.168.100.59:3000/location', { isMobile, device: data });
    } else { 
      await axios.post('http://localhost:3000/location', { isMobile, device: data });
    }

    setLat(`lat: ${data.location.lat}`)
    setLog(`log: ${data.location.lng}`)
    setRv(sv);
    alert('Sucesso');
  }

  useEffect(() => {

  });

  return (
    <div className="App">
      <header className="App-header">
       <button onClick={ handleLocation }>Get your Location</button>
        <p>
          {lat && log ? `${lat} ${log}` : 'Click the button to get your location'}
        </p>
      </header>
    </div>
  );
}

export default App;
